# Install Google Chrome
$LocalTempDir = $env:TEMP; $ChromeInstaller = "ChromeInstaller.exe"; (new-object    System.Net.WebClient).DownloadFile('http://dl.google.com/chrome/install/375.126/chrome_installer.exe', "$LocalTempDir\$ChromeInstaller"); & "$LocalTempDir\$ChromeInstaller" /silent /install; $Process2Monitor =  "ChromeInstaller"; Do { $ProcessesFound = Get-Process | ?{$Process2Monitor -contains $_.Name} | Select-Object -ExpandProperty Name; If ($ProcessesFound) { "Still running: $($ProcessesFound -join ', ')" | Write-Host; Start-Sleep -Seconds 2 } else { rm "$LocalTempDir\$ChromeInstaller" -ErrorAction SilentlyContinue -Verbose } } Until (!$ProcessesFound)

# Install Traps Agent
$LocalTempDir = $env:TEMP; $TrapsInstaller = "TrapsInstaller.msi"; (new-object    System.Net.WebClient).DownloadFile('https://s3.amazonaws.com/cortexlab-conf/opt/TrapsInstaller.msi', "$LocalTempDir\$TrapsInstaller"); & msiexec /i "$LocalTempDir\$TrapsInstaller" /l*v "$LocalTempDir\trapsinstall.log" /qn; Start-Sleep -Seconds 10
